mapper.update:
update sys_role set
	role_id=#{role_id}

		,role_name = #{role_name}


		,role_desc = #{role_desc}


		,rights = #{rights}


		,add_qx = #{add_qx}


		,del_qx = #{del_qx}


		,edit_qx = #{edit_qx}


		,query_qx = #{query_qx}

	where 1=1 and role_id=#{role_id}

mapper.insert:
insert into sys_role(
	role_name,
	role_desc,
	user_id
)values(
	#{role_name},
	#{role_desc},
	#{user_id}
)
